DROP TABLE IF EXISTS "auth_group";
CREATE TABLE "auth_group" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(80) NOT NULL UNIQUE);
DROP TABLE IF EXISTS "auth_group_permissions";
CREATE TABLE "auth_group_permissions" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "group_id" integer NOT NULL REFERENCES "auth_group" ("id"), "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id"));
DROP TABLE IF EXISTS "auth_permission";
CREATE TABLE "auth_permission" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "content_type_id" integer NOT NULL REFERENCES "django_content_type" ("id"), "codename" varchar(100) NOT NULL, "name" varchar(255) NOT NULL);
DROP TABLE IF EXISTS "auth_user";
CREATE TABLE "auth_user" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "password" varchar(128) NOT NULL, "last_login" datetime NULL, "is_superuser" bool NOT NULL, "first_name" varchar(30) NOT NULL, "last_name" varchar(30) NOT NULL, "email" varchar(254) NOT NULL, "is_staff" bool NOT NULL, "is_active" bool NOT NULL, "date_joined" datetime NOT NULL, "username" varchar(30) NOT NULL UNIQUE);
DROP TABLE IF EXISTS "auth_user_groups";
CREATE TABLE "auth_user_groups" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "user_id" integer NOT NULL REFERENCES "auth_user" ("id"), "group_id" integer NOT NULL REFERENCES "auth_group" ("id"));
DROP TABLE IF EXISTS "auth_user_user_permissions";
CREATE TABLE "auth_user_user_permissions" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "user_id" integer NOT NULL REFERENCES "auth_user" ("id"), "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id"));
DROP TABLE IF EXISTS "django_admin_log";
CREATE TABLE "django_admin_log" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "object_id" text NULL, "object_repr" varchar(200) NOT NULL, "action_flag" smallint unsigned NOT NULL, "change_message" text NOT NULL, "content_type_id" integer NULL REFERENCES "django_content_type" ("id"), "user_id" integer NOT NULL REFERENCES "auth_user" ("id"), "action_time" datetime NOT NULL);
DROP TABLE IF EXISTS "django_content_type";
CREATE TABLE "django_content_type" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "app_label" varchar(100) NOT NULL, "model" varchar(100) NOT NULL);
DROP TABLE IF EXISTS "django_migrations";
CREATE TABLE "django_migrations" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "app" varchar(255) NOT NULL, "name" varchar(255) NOT NULL, "applied" datetime NOT NULL);
DROP TABLE IF EXISTS "django_session";
CREATE TABLE "django_session" ("session_key" varchar(40) NOT NULL PRIMARY KEY, "session_data" text NOT NULL, "expire_date" datetime NOT NULL);
DROP TABLE IF EXISTS "sales_categoryofaggregation";
CREATE TABLE "sales_categoryofaggregation" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(200) NOT NULL, "code" varchar(5) NULL, "description" text NOT NULL);
DROP TABLE IF EXISTS "sales_component";
CREATE TABLE "sales_component" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(200) NOT NULL, "code" varchar(5) NULL, "description" text NOT NULL);
DROP TABLE IF EXISTS "sales_concattable";
CREATE TABLE "sales_concattable" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(200) NOT NULL, "code" varchar(7) NOT NULL, "concat_price" smallint unsigned NOT NULL, "description" text NOT NULL, "avail_from" smallint unsigned NOT NULL, "avail_to" smallint unsigned NOT NULL, "frequency_id" integer NOT NULL REFERENCES "sales_frequency" ("id"), "level_of_aggregation_id" integer NOT NULL REFERENCES "sales_levelofaggregation" ("id"), "table_id" integer NOT NULL REFERENCES "sales_table" ("id"));
DROP TABLE IF EXISTS "sales_concattable_variable";
CREATE TABLE "sales_concattable_variable" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "concattable_id" integer NOT NULL REFERENCES "sales_concattable" ("id"), "variable_id" integer NOT NULL REFERENCES "sales_variable" ("id"));
DROP TABLE IF EXISTS "sales_database";
CREATE TABLE "sales_database" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(200) NOT NULL, "code" varchar(3) NULL, "description" text NOT NULL, "component_id" integer NOT NULL REFERENCES "sales_component" ("id"));
DROP TABLE IF EXISTS "sales_factor";
CREATE TABLE "sales_factor" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "f" varchar(15) NOT NULL, "value" smallint unsigned NOT NULL);
DROP TABLE IF EXISTS "sales_frequency";
CREATE TABLE "sales_frequency" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(200) NOT NULL, "code" varchar(5) NULL, "description" text NOT NULL, "price" smallint unsigned NOT NULL);
DROP TABLE IF EXISTS "sales_levelofaggregation";
CREATE TABLE "sales_levelofaggregation" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(200) NOT NULL, "code" varchar(5) NULL, "description" text NOT NULL);
DROP TABLE IF EXISTS "sales_levelofaggregation_category_of_aggregation";
CREATE TABLE "sales_levelofaggregation_category_of_aggregation" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "levelofaggregation_id" integer NOT NULL REFERENCES "sales_levelofaggregation" ("id"), "categoryofaggregation_id" integer NOT NULL REFERENCES "sales_categoryofaggregation" ("id"));
DROP TABLE IF EXISTS "sales_period";
CREATE TABLE "sales_period" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "period_from" smallint unsigned NOT NULL, "period_to" smallint unsigned NOT NULL, "description" text NOT NULL, "factor_id" integer NOT NULL UNIQUE REFERENCES "sales_factor" ("id"));
DROP TABLE IF EXISTS "sales_table";
CREATE TABLE "sales_table" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(200) NOT NULL, "code" varchar(3) NOT NULL, "description" text NOT NULL, "database_id" integer NOT NULL REFERENCES "sales_database" ("id"));
DROP TABLE IF EXISTS "sales_table_frequency";
CREATE TABLE "sales_table_frequency" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "table_id" integer NOT NULL REFERENCES "sales_table" ("id"), "frequency_id" integer NOT NULL REFERENCES "sales_frequency" ("id"));
DROP TABLE IF EXISTS "sales_table_level_of_aggregation";
CREATE TABLE "sales_table_level_of_aggregation" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "table_id" integer NOT NULL REFERENCES "sales_table" ("id"), "levelofaggregation_id" integer NOT NULL REFERENCES "sales_levelofaggregation" ("id"));
DROP TABLE IF EXISTS "sales_userrequest";
CREATE TABLE "sales_userrequest" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "user" varchar(200) NOT NULL, "table" varchar(200) NOT NULL, "level_of_agregation" varchar(100) NOT NULL, "frequency" varchar(50) NOT NULL, "code" varchar(10) NOT NULL, "concat_price" smallint unsigned NOT NULL, "years" varchar(200) NOT NULL, "variables" varchar(200) NOT NULL, "categories_of_aggregation" varchar(200) NOT NULL, "request_time" datetime NOT NULL, "data_price" integer NOT NULL, "purchased" bool NOT NULL);
DROP TABLE IF EXISTS "sales_variable";
CREATE TABLE "sales_variable" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(200) NOT NULL, "code" varchar(5) NOT NULL, "price" integer NOT NULL, "description" text NOT NULL);
